                  onChange={handelchange} 
              id="number" placeholder="Enter Your Mobile" />
              <br />
  
              <label htmlFor="">Address</label>
              <textarea
                type="text"
                name="userAddress"
                  value={user.userAddress}
                  onChange={handelchange}
                placeholder="Enter Your Address"
                id="address"
              />
              <br /> 
             
               <button onClick={postData} >
               <a href="/res"></a>
               </button> 
             </form>
          </div> 
         </div> 
      </>
