import { useEffect, useState } from "react";

import {useNavigate} from "react-router-dom"
export const Book = () => {
  let [data, setdata] = useState([{}]);
  let url = "http://localhost:5000/books";
  
  let navigate =useNavigate()
  useEffect(() => {
    getdata();
  }, []);

  async function getdata() {
    let response = await fetch(url);
    let Data = await response.json();
    setdata(Data);
  }
  console.log(data);
  data.map((item) => {
    console.log(item.bookName);
  });
  function buyBook() {
    // console.log(id);
    navigate('/order')
  }
  return (
    <>
      <div className="search_div">
        <input type="text" />
        <button>Search</button>
      </div>
      <div className="main_dilip">
        {data.map((data,index) => (
          <div className="dilip" key={item.id}>
            <img src={data.bookimage} alt="" />

            <p>Bookname:{data.bookName}</p>
            <p>Authher:{data.autherName}</p>
            <p>Rs:{data.bookPrice}</p>
            <button>AddtoCart</button>
            <button onClick={buyBook}>Buy</button>
          </div>
        ))}
      </div>
    </>
  );
};
