import {useLocation} from 'react-router-dom';
import { useState } from 'react';
//  function ComponentB() {

    
    
    export const Order = () => {
    const location = useLocation();
    // console.log('props:', props)
    // console.log(location.state)
    

    
const navigate = useNavigate();
    
    
    const [user, setuser] = useState({
      firstname: "",
      lastname: "",
      email: "",
      password: ""
    });
    let name, value;
    const handelchange = (e) => {
      // console.log(e);
      name = e.target.name;
      value = e.target.value;
    
      setuser({ ...user, [name]: value });
    };
    
    const postData = async (e) => {
      e.preventDefault();
      const { firstname, lastname, email, password } = user;
    
      const res = await fetch("/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          firstname,
          lastname,
          email,
          password
        })
      });
      const data = await res.json();
      if (data.status === 422 || !data) {
        window.alert("registration failed");
        console.log("faild");
      } else {
        window.alert("registration success");
        console.log("success");
        // history.push("/login");
        navigate('/Login')
      }
    };









    return (
      <>
      <div className="bookDetalis">

         <img src={location.state.img} alt="" />
       <div className="text_div">

        <span>Book Name:{location.state.name}</span>
        <span>BookAuther:{location.state.auther}</span> 
        <span>Rs:{location.state.price}</span> 
       </div>
      </div>
         <div className="oderer_data">
          <div className="oderer_Form">
            <form method="Post">
              <label htmlFor="">Name</label>
              <input type="text" placeholder="Enter Your Name" id="name" />
              <br />
  
              <label htmlFor="">Email</label>
              <input type="Email" placeholder="Enter Your Email" id="email" />
              <br />
              <label htmlFor="">Phone</label>
              <input type="Number" id="number" placeholder="Enter Your Mobile" />
              <br />
  
              <label htmlFor="">Address</label>
              <textarea
                type="text"
                placeholder="Enter Your Address"
                id="address"
              />
              <br /> 
               {/* <h1>{props[0].booName}</h1>  */}
               <button onClick={postData} >Order Place</button> 
             </form>
          </div> 
         </div> 
      </>
    );
  };
