import {useLocation} from 'react-router-dom';

//  function ComponentB() {

    
    
    export const Order = () => {
    const location = useLocation();
    // console.log('props:', props)
    // console.log(location.state)
    return (
      <>
      <div className="bookDetalis">

       <img src={location.state.img} alt="" />
     
       <span>Book Name:{location.state.name}</span>
        <span>{location.state.auther}</span> 
      </div>
         <div className="oderer_data">
          <div className="oderer_Form">
            <form action="Post">
              <label htmlFor="">Name</label>
              <input type="text" placeholder="Enter Your Name" id="name" />
              <br />
  
              <label htmlFor="">Email</label>
              <input type="Email" placeholder="Enter Your Email" id="email" />
              <br />
              <label htmlFor="">Phone</label>
              <input type="Number" id="number" placeholder="Enter Your Mobile" />
              <br />
  
              <label htmlFor="">Address</label>
              <textarea
                type="text"
                placeholder="Enter Your Address"
                id="address"
              />
              <br /> 
               {/* <h1>{props[0].booName}</h1>  */}
               <button >Order Place</button> 
             </form>
          </div> 
         </div> 
      </>
    );
  };
