import { useEffect, useState } from "react";
import {Link, useNavigate} from 'react-router-dom';
import {Order} from './Order'

export const Book = (probs) => {
  let [data, setdata] = useState([{}]);
  let [buydata, setbuydata] = useState([{}]);
  let url = "http://localhost:5000/";
  
  let navigate =useNavigate();

  useEffect(() => {
    getdata();
  }, []);

//   -----
// const toComponentB=(id,img,name,auther,price)=>{
const toComponentB=(d)=>{
    navigate('/order',
    // {state:{id:id,img:img,name:name,auther:auther,price:price}}
    {state:{id:id,img:img,name:name,auther:auther,price:price}}
    // {state:buydata.json()}
    );
      }


  async function getdata() {
    let response = await fetch(url);
    let Data = await response.json();
    setdata(Data);
  }


  function buyBook(id,img,name,auther,price) {
       
    //   console.log('d:', d)
   
      toComponentB(id,img,name,auther,price)
   
      
      
    }

  return (
    <>
       
       
      <div className="search_div">
        <input type="text" />
        <button>Search</button>
      </div>
      <div className="main_dilip">
            {data.map((data,index) => (
                <div className="dilip" key={index}>
                <img src={data.bookimage} alt="" />

            <p>Bookname:{data.bookName}</p>
            <p>Authher:{data.autherName}</p>
            <p>Rs:{data.bookPrice}</p>
            <h1>

            </h1>
            <button>AddtoCart</button>
            <button onClick={()=>buyBook(data)
                // {data._id,data.bookimage,data.bookName,data.autherName.data.bookPrice}
            }>Buy</button>
            
           </div> 
         ))} 
         
       </div>
    </>
  );
  
}

