import {useLocation} from 'react-router-dom';
import { useState,useNavigate } from 'react';
//  function ComponentB() {

    
    
    export const Order = () => {
    const location = useLocation();
    // console.log('props:', props)
    // console.log(location.state)
    

    
// const navigate = useNavigate();
    
    
    const [user, setuser] = useState({
        userName:"",
         userEmail:"",
          userMobile:"", 
          userAddress:""
    });
    let name, value;
    const handelchange = (e) => {
      // console.log(e);
      name = e.target.name;
      value = e.target.value;
    
      setuser({ ...user, [name]: value });
    };
    
    const postData = async (e) => {
      e.preventDefault();
      const { userName, userEmail, userMobile, userAddress } = user;
    
      const res = await fetch("http://localhost:5000/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
            userName,
             userEmail,
              userMobile,
               userAddress
        })
      });
      const data = await res.json();
      if (data.status === 422 || !data) {
        window.alert("registration failed");
        console.log("faild");
      } else {
        window.alert("registration success");
        console.log("success");
        // history.push("/login");
        // navigate('/Login')
      }
    };

 return (
      <>
      <div className="bookDetalis">

         <img src={location.state.img} alt="" />
       <div className="text_div">

        <span>Book Name:{location.state.name}</span>
        <span>BookAuther:{location.state.auther}</span> 
        <span>Rs:{location.state.price}</span> 
       </div>
      </div>
         <div className="oderer_data">
          <div className="oderer_Form">

            <form onSubmit={handleSubmit}>
              <label htmlFor="">Name</label>
              <input type="text"
                name="userName"
                value={user.userName}
                  onChange={handelchange}
               placeholder="Enter Your Name" id="name" />
              <br />
  
              <label htmlFor="">Email</label>
              <input type="Email"
                name="userEmail"
                  value={user.userEmail}
                  onChange={handelchange} 
              placeholder="Enter Your Email" id="email" />
              <br />
              <label htmlFor="">Phone</label>
              <input type="Number"
                name="userMobile"
                  value={user.userMobile}
                  onChange={handelchange} 
              id="number" placeholder="Enter Your Mobile" />
              <br />
  
              <label htmlFor="">Address</label>
              <textarea
                type="text"
                name="userAddress"
                  value={user.userAddress}
                  onChange={handelchange}
                placeholder="Enter Your Address"
                id="address"
              />
              <br /> 
             
               <button   >
              Order Place
               </button> 
             </form>
          </div> 
         </div> 
      </>
    );
  };
