import { useEffect, useState } from "react";
import {Link, useNavigate} from 'react-router-dom';
import {Order} from './Order'

export const Book = (probs) => {
  let [data, setdata] = useState([{}]);
//   let [buydata, setbuydata] = useState([{}]);
  let url = "http://localhost:5000/";
  
  let navigate =useNavigate();

  useEffect(() => {
    getdata();
  }, []);

//   -----
const toComponentB=()=>{
    navigate('/order',{state:{id:1,name:'sabaoon'}});
      }


  async function getdata() {
    let response = await fetch(url);
    let Data = await response.json();
    setdata(Data);
  }
//   console.log(data);
//   data.map((item) => {
//     console.log(item.bookName);
//   });

  function buyBook(d) {
       
      console.log('d:', d)
    console.log(d);
    setbuydata(d)
    

        // <Order name={d} />
        // navigate('/order',{ state: { name:'Xyz' }})
    
    
  }
//   function child() {
//     // console.log(id);
//     alert("hi dilip")
//     // navigate('/order')
//   }
  return (
    <>
        <a onClick={()=>{toComponentB()}}>Component B</a>
       
      <div className="search_div">
        <input type="text" />
        <button>Search</button>
      </div>
      <div className="main_dilip">
            {data.map((data,index) => (
                <div className="dilip" key={index}>
                <img src={data.bookimage} alt="" />

            <p>Bookname:{data.bookName}</p>
            <p>Authher:{data.autherName}</p>
            <p>Rs:{data.bookPrice}</p>
            <h1>

            </h1>
            <button>AddtoCart</button>
            <button onClick={()=>buyBook({data})}>Buy</button>
            
             {/* <button onClick={()=>navigate('/order',{data})}>Buy</button> */} 
           </div> 
         ))} 
         
               {/* { <Order name={data}  />     */}
       </div>
    </>
  );
  
        }
